import { useState } from "react";
import { ethers, BigNumber } from "ethers";
import Token from "../../contracts/Token.json";
import { proof, isIncludes, whiteList } from "./data";

const addressForTest = "0x2fc5fF3c967274E0A32F64eA031CA886A7e7BC43";

const MainMit = ({ accounts }) => {
  console.log("accounts",accounts)
  const [flag, setFlag] = useState(false);
  const [value, setValue] = useState(1);
  const isConnected = Boolean(accounts[0]);
  const isWhiteList = useState(false)

  async function handleMint() {
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(
        addressForTest, // 合约地址
        Token.abi,
        signer
      );
      try {
        if (flag) {
          return;
        }
        setFlag(true);

        const name = await contract.name();
        setFlag(false);
        console.log(name, "name");
      } catch (err) {
        console.log("error", err);
      }
    }
  }

  async function handleMintGuest() {
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(
        addressForTest, // 合约地址
        Token.abi,
        signer
      );
      try {
        if (flag) {
          return;
        }
        setFlag(true);
        const response = await contract.mintGuest(BigNumber.from(value), {
          value: ethers.utils.parseEther((0.08 * value).toString()),
        });
        window.alert("success!");
        setFlag(false);
        console.log("response", response);
      } catch (err) {
        console.log("error", err);
        window.alert("fail!");
      }
    }
  }
  async function handleMintWhiteList() {
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(
        addressForTest, // 合约地址
        Token.abi,
        signer
      );
      try {
        if (flag) {
          return;
        }
        setFlag(true);
        await contract.mintWhiteList(proof, value, {
          value: ethers.utils.parseEther((0.06 * value).toString()),
        });
        setFlag(false);
      } catch (err) {
        console.log("error", err);
      }
    }
  }

  // -
  const handlerDecrement = () => {
    if (value - 1 === 0) {
      console.log(value, "value");
      return;
    }
    setValue((pre) => pre - 1);
  };
  // +
  const handlerIncrement = () => {
    if (value + 1 === 6) {
      console.log(value, "value");
      return;
    }
    setValue((pre) => pre + 1);
  };

  return {
    handleMint,
    handlerDecrement,
    handlerIncrement,
    isConnected,
    flag,
    handleMintGuest,
    handleMintWhiteList,
    isWhiteList: ()=> whiteList.includes(accounts[0]?.toString()),
    value,
  };
};

export default MainMit;
